package com.syntax.class02;

public class ArithmeticTask1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=10, num2=5;
		int add=num1+num2;
		int sub=num1-num2;
		int mul=num1*num2;
		int div=num1/num2;
		System.out.println("The adding of 2 nembers "+num1+" and "+num2+" ="+add);
		System.out.println("The subtraction of 2 nembers "+num1+" and "+num2+" ="+sub);
		System.out.println("The multiplication of 2 nembers "+num1+" and "+num2+" ="+mul);
		System.out.println("The division of 2 nembers "+num1+" and "+num2+" ="+div);

		

	}

}
